import React, { Component } from "react";
import { Formik, Form, Field } from "formik";
import Axios from "axios";
import * as yup from "yup";
import { connect } from "react-redux";
import swal from "sweetalert";
import { Redirect } from "react-router-dom";

class DangNhap extends Component {
  _handleSubmit = (values) => {
    // const xsrfToken = this.getCookie("XSRF-TOKEN");
    var postData = values;
    let axiosConfig = {
      headers: {
        "Content-Type": "application/json;charset=UTF-8",
        // "X-XSRF-TOKEN": xsrfToken,
        "Access-Control-Allow-Origin": "*",
      },
    };
    Axios.post("/api/login", postData, axiosConfig)
      .then((res) => {
        if (res.data.status === "true") {
          this.props.dispatch({
            type: "CHANGE_LOGIN_STATUS",
          });
          localStorage.setItem('email',values.email);
          this.props.history.push("/");
        } else {
          swal({
            title: "Lỗi Đăng Nhập",
            text: res.data.message,
            icon: "warning",
          });
        }
      })
      .catch((err) => {
        console.log(err);
      });
    console.log(values);
  };

  render() {
    
    return (
      <div className="login-page">
        <div
          className="page-header header-filter"
          style={{
            backgroundImage: "url(./assets/img/bg7.jpg)",
            backgroundSize: "cover",
            backgroundPosition: "top center",
          }}
        >
          <div className="container">
            <div className="row">
              <div className="col-md-4 col-sm-6 ml-auto mr-auto">
                <div className="card card-signup">
                  <Formik
                    initialValues={{
                      email: "",
                      password: "",
                    }}
                    onSubmit={this._handleSubmit}
                    render={({ handleChange }) => (
                      <Form className="form">
                        <div className="card-header card-header-primary text-center">
                          <h4 className="card-title">Đăng nhập</h4>
                          <div className="social-line">
                            <a
                              href="#pablo"
                              className="btn btn-just-icon btn-link"
                            >
                              <i className="fa fa-facebook-square" />
                            </a>
                            <a
                              href="#pablo"
                              className="btn btn-just-icon btn-link"
                            >
                              <i className="fa fa-twitter" />
                            </a>
                            <a
                              href="#pablo"
                              className="btn btn-just-icon btn-link"
                            >
                              <i className="fa fa-google-plus" />
                            </a>
                          </div>
                        </div>
                        <div className="card-body">
                          <div className="input-group">
                            <span className="input-group-addon">
                              <i className="material-icons">face</i>
                            </span>
                            <Field
                              type="text"
                              name="email"
                              className="form-control"
                              onChange={handleChange}
                              placeholder="Email..."
                            />
                          </div>
                          <div className="input-group">
                            <span className="input-group-addon">
                              <i className="material-icons">lock_outline</i>
                            </span>
                            <Field
                              type="password"
                              name="password"
                              className="form-control"
                              onChange={handleChange}
                              placeholder="Mật khẩu..."
                            />
                          </div>
                        </div>
                        <div className="text-center">
                          <button
                            style={{ marginTop: "32px", marginBottom: "32px" }}
                            type="submit"
                            className="btn btn-danger"
                          >
                            Đăng nhập
                          </button>
                        </div>
                      </Form>
                    )}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    loginStatus: state.loginReducer,
  };
};
export default connect(mapStateToProps)(DangNhap);
